INSERT INTO `News` (`id`, `type`, `title`, `link`, `description`, `time`) VALUES
(1, 'Audition Update', 'January 27, 2011 Patch', '', '- 7 songs added (<a href="/music/list/">updated Song List</a>)\r\n- 90 new items including 3 faces and 4 motions\r\n\r\n<a href="/patch/20110127/">More Info</a>', 1297313794),
(2, 'Site Update', 'Audifan Changes', '', '<ul>\r\n<li>General changes</li>\r\n	<ul>\r\n	<li>Updated page style/layout.</li>\r\n	<li>Tables on the site (e.g. the EXP table, license statistics table, song list, etc.) now have separating lines for easier reading.</li>\r\n	<li>The playlist on the main page has been removed.  It will be replaced with an upcoming new feature.</li>\r\n	</ul>\r\n\r\n<li>Patch Information changes</li>\r\n	<ul>\r\n	<li>New patch information layout. Instead of showing patch information in sections (such as New Songs or New Items), we are starting to make a list of new features and how to use them, similar to the way Audition publishers post their patch notes.  We are still in the process of re-formatting, and in some cases finding, patch information for all patches in the past.</li>\r\n		<ul>\r\n		<li>Edit: All Redbana patches have been formatted and re-added to the site.  Nexon patches are still being worked on.</li>\r\n		<li>Edit #2: All patches have been re-added to the site!</li>\r\n		</ul>\r\n	</ul>\r\n\r\n<li>Guide changes</li>\r\n	<ul>\r\n	<li>The EXP table now shows the EXP gaps between levels.  It also now shows all levels.</li>\r\n	<li>The license statistics table has been updated to reflect the license changes from the patch on May 25.  Levels 76 through Max will be posted as soon as possible.</li>\r\n		<ul>\r\n		<li>Edit: Levels 76 through Max have been added to the license guide.</li>\r\n		</ul>\r\n	<li>The story guide no longer can be sorted because most of the values for EXP and Beats earned were found to be incorrect.  It may be re-added if we can confirm the correct values.</li>\r\n	<li>The couple guide has been dramatically updated.</li>\r\n		<ul>\r\n		<li>Wedding attempt information has been updated.</li>\r\n		<li>Love Party information has been updated with the correct information and missing information has been added.</li>\r\n		<li>A hearts table has been added with required hearts and gaps between levels.</li>\r\n		<li>Images of rings and couple level titles have been added.</li>\r\n		</ul>\r\n	</ul>\r\n\r\n<li>Music changes</li>\r\n	<ul>\r\n	<li>The song list can now be instantly sorted (without reloading the page) by artist, title, or BPM.  Sorting the table doesn''t seem to be working in Internet Explorer.  We''re working on a fix.</li>\r\n		<ul>\r\n		<li>Edit: If you are having an issue with sorting the table in Internet Explorer, try updating to Internet Explorer 8.</li>\r\n		</ul>\r\n	</ul>\r\n</ul>\r\n\r\nSome of these changes may seem like we''re making the site smaller, but that''s far from where we''re headed.\r\n\r\nAfter the release of these changes, we plan on doing some pretty cool things to make your Audition experience better than ever!\r\n\r\nPlease let us know what you think of the site via our <a href="/contactus/">Contact Us page</a>!', 1297313794),
(3, 'Site Update', 'New Site Map', '/sitemap/', '', 1297313794),
(4, 'Site News', 'Contact Us form fixed', '', 'The <a href="/contactus/">Contact Us form</a> has been fixed.  You will no longer submit to a page that doesn''t exist.  Sorry about that!', 1297313794),
(5, 'Site Update', 'News Update & Facebook Like Buttons', '', 'We have updated our site with some new features!\r\n<ul>\r\n<li>News Update</li>\r\n	<ul>\r\n	<li>News items on the homepage now link to a more informative description.</li>\r\n	<li>News items will now be archived on the <a href="/news/">News Homepage</a>.</li>\r\n	</ul>\r\n<li>Facebook Like Buttons</li>\r\n	<ul>\r\n	<li>Facebook Like Buttons are now on Audifan!</li>\r\n	<li>Like buttons will be added to more pages in the future.</li>\r\n	</ul>\r\n</ul>', 1297454252),
(6, 'Audition Update', 'Predicted March 10 Patch', '', 'We''ve added patch notes for Redbana''s expected March 10 patch.\r\n\r\nThese patch notes are not guaranteed nor confirmed to be accurate.  They will be changed as necessary!\r\n\r\n<a href="/patch/20110310/">March 10, 2011 Patch</a>', 1298690658),
(7, 'Site Update', 'Updated Story Guide', '', 'The <a href="/guides/story/">Story Guide</a> has been updated with a few song changes.', 1298779075),
(8, 'Audition News', '2 Free Magic Lamps', '', 'Redbana is giving you the chance to get 2 free magic lamps after the <a href="/patch/20110310/">March 10 Patch</a>!\r\n\r\nStarting after March 10, simply log in for 7 days to receive one magic lamp and log in for 14 days to receive two!\r\n\r\n<a href="http://forums.redbana.com/showthread.php?t=36801" target="_blank">More Info</a>', 1299017103),
(9, 'Audition Update', 'March 10, 2011 Patch', '', '- Couple Battle Party update\r\n- Couple Synergy update\r\n- FAM Battle update\r\n- 11 songs added (<a href="/music/list/">Updated Song List</a>)\r\n- Re-added 13 songs that were previously removed\r\n- 98 new items including 3 faces and 4 motions\r\n\r\n<a href="/patch/20110310/">More Info and Details</a>', 1299775205),
(10, 'Audition News', 'Redbana Website Down', '', 'Redbana''s website appears to be down in some areas due to a DNS error.\r\n\r\nDNS (domain name system) is used to change a domain name (redbana.com) to its IP address in order to properly navigate to the server on the Internet.\r\n\r\nEdit (3/18): The site seems to be slowly coming back to normal.', 1300385150),
(11, 'Audition Update', 'Predicted April Patch', '', 'We''ve added some info about the April 2011 patch!  Redbana has said that the patch should be anytime next week, so the April 7 date is also a prediction.\r\n\r\nThese patch notes are only predictions and are not guaranteed!\r\n\r\n<a href="/patch/20110407/">April 7, 2011 Patch</a>\r\n\r\nEdit (4/1): The April patch is confirmed to be planned for April 7!', 1301552081),
(12, 'Audition News', 'Audition Anniversary / GoCash Promotion', '', 'Redbana is celebrating Audition''s second anniversary with a ton of giveaways!\r\n\r\nFirst, simply by logging in to the game between April 7 and April 30, you will receive a special anniversary t-shirt.\r\n\r\nSecond, if you log in at least 7 days between the 7 and 30, you will receive a free cotton candy accessory.\r\n\r\nFinally, if you log in at least 14 days between the 7 and 30, you will receive a free cake pet.\r\n\r\nMore info about the Audition Second Anniversary Celebration:\r\n<a href="http://forums.redbana.com/showthread.php?t=38227" target="_blank">http://forums.redbana.com/showthread.php?t=38227</a>\r\n\r\n\r\nIn addition to the Anniversary Events planned, you can also receive bonuses by redeeming GoCash cards in April!\r\n\r\nEveryday in April, redeeming GoCash cards can get you some magic lamps in-game.\r\n\r\nMore info about the April GoCash Promotion:\r\n<a href="http://www.gocashgamecard.com/events/event_view.aspx?id=100" target="_blank">http://www.gocashgamecard.com/events/event_view.aspx?id=100</a>', 1301702271),
(13, 'Site Update', '4/4 "Core" Update', '', 'We did a pretty major update today in order to prepare for an upcoming new feature.  They were mostly behind-the-scenes updates, so you can''t really see that anything has changed.\r\n\r\nSome visible changes you may notice include a new scroller at the top of each page that we can use to pass important info along to you.  Also, there''s currently a large space in between the "Home" and the "Guides" tabs on the menu.  It''s simply the empty space for the tab of the aforementioned new feature when it''s ready to be released.  There''s nothing wrong with your browser!\r\n\r\nAs always, thank you for visiting Audifan!', 1301947142),
(14, 'Audition Update', 'April 7, 2011 Patch', '', '- Space Pang Pang Mode\r\n- Couple Ring Box\r\n- 10 Songs Added\r\n- 100 Items added\r\n- Diary\r\n\r\n<a href="/patch/20110407/">View More Details and Info</a>!', 1302202312),
(15, 'Site Update', 'EXP/Beats Added to Story Guide', '', 'The <a href="/guides/story/">story guide</a> now shows the EXP and Beats earned for each story!\r\n\r\nIt also now has an explanation for how EXP/Beats are reduced as you complete a story multiple times.', 1302642050),
(16, 'Site News', 'We Need Beta Testers!', '', 'Are you interested in testing new features on Audifan?\r\n\r\nJoin the Audifan Beta Testers group on the Redbana Forums!\r\n\r\n<a href="http://forums.redbana.com/group.php?groupid=242" target="_blank">http://forums.redbana.com/group.php?groupid=242</a>', 1302763632),
(17, 'Site Update', 'Profiles and More!', '', 'Profiles are here!  We hope these new profiles give you the opportunity to find old friends and new friends in the North-American Audition community!\r\n\r\nIn addition to profiles, we have updated many existing features of the site.  Here''s a complete list of changes to the site:\r\n\r\n<ul>\r\n<li>Audifan Accounts</li>\r\n	<ul>\r\n	<li>Create an Audifan account <a href="/account/register/">here</a>!</li>\r\n	<li>Once you create an account, you must verify your e-mail address before you can log in.</li>\r\n	<li>Please review our new <a href="/termsofuse/">terms of use</a>.</li>\r\n	</ul>\r\n\r\n<li>Profiles</li>\r\n	<ul>\r\n	<li>Most account/profile actions can be found at the very top of any page.</li>\r\n	<li>Edit the layout of your profile, your Audition character info, and your profile picture from the <a href="/community/profile/edit/">Edit Profile page</a>.</li>\r\n	<li>Add/remove songs to your favorites list from the <a href="/music/list/">song list</a>.</li>\r\n	</ul>\r\n\r\n<li>Community</li>\r\n	<ul>\r\n	<li>The <a href="/community/">community homepage</a> shows:</li>\r\n		<ul>\r\n		<li>the newest member.</li>\r\n		<li>a random member.</li>\r\n		<li>who''s currently online.</li>\r\n		</ul>\r\n	<li><a href="/community/search/">Community search</a> lets you search for people by display name, IGN, FAM, or all 3.</li>\r\n	</ul>\r\n\r\n<li>Guide Changes</li>\r\n	<ul>\r\n	<li>EXP Table</li>\r\n		<ul>\r\n		<li>There are now 2 tables side-by-side to reduce the length of the guide.</li>\r\n		<li>If you have entered your level in your character info, your next level will be highlighted.</li>\r\n		</ul>\r\n	<li>License Guide</li>\r\n		<ul>\r\n		<li>The table now has its own scrollbar.</li>\r\n		<li>If you have entered your level in your character info, your next level''s license will be highlighted.</li>\r\n		</ul>\r\n	<li>Story Guide</li>\r\n		<ul>\r\n		<li>The table now has its own scrollbar.</li>\r\n		</ul>\r\n	<li>Couple Guide</li>\r\n		<ul>\r\n		<li>The hearts table is also now 2 tables side-by-side.</li>\r\n		<li>If you have entered your couple level, your next couple level will be highlighted, as well as your next Love Party.</li>\r\n		</ul>\r\n	</ul>\r\n\r\n<li>Contact Us</li>\r\n	<ul>\r\n	<li>If logged in, your e-mail address will automatically be entered.</li>\r\n	</ul>\r\n</ul>', 1303361485),
(18, 'Site Update', 'New Tournament Time Guide', 'http://www.auditionfan.com/guides/tournamenttime/', '', 1304204633),
(21, 'Audition News', 'May GoCash Promotion!', '', 'Throughout the month of May, you can get free pets in-game for buying GoCash cards.\r\n\r\nEvery $15 you spend gets you a free pet!\r\n\r\nAdditionally, the top spender in May will win a $50 GameStop gift card!\r\n\r\nMore information here:\r\n<a href="http://www.gocashgamecard.com/events/event_view.aspx?id=107" target="_blank">http://www.gocashgamecard.com/events/event_view.aspx?id=107</a>', 1304453395),
(22, 'Audition Update', 'May 12, 2011 Patch', '', '- Rhythm Holic mode\r\n- Happy Box\r\n- 5 New Songs\r\n- 115 New Items\r\n- New FAM Items\r\n\r\n<a href="/patch/20110512/">View Complete Patch Info</a>', 1305249697),
(23, 'Audition Update', 'Predicted June Patch', '', 'We''ve added some info about the June patch, including a guide on how to play the new Guitar Mode that will be added!\r\n\r\nThese notes are only predictions, and are not guaranteed!\r\n\r\n<a href="/patch/20110609/">June 9, 2011 Patch</a>', 1307014230),
(24, 'Audition News', 'Guitar Mode Lauching Celebration', '', 'With the launch of Guitar Mode, Redbana is giving you the chance to win a wardrobe to match!\r\n\r\nSimply by logging in for 7 days, between June 9 and June 30, you will receive an in-game guitar!\r\n\r\nLog in for 14 days and you''ll get a complete rockstar set (hair, top, bottom, and shoes).\r\n\r\nCheck out pictures of these items and more information here:\r\n<a href="http://forums.redbana.com/showthread.php?t=41873" target="_blank">http://forums.redbana.com/showthread.php?t=41873</a>', 1307014789),
(25, 'Audition News', 'June GoCash Promotion', '', 'During the month of June, you can get one of five exclusive wings accessories by redeeming GoCash cards!\r\n\r\nMore information: <a href="http://forums.redbana.com/showthread.php?t=41868" target="_blank">http://forums.redbana.com/showthread.php?t=41868</a>', 1307015005),
(26, 'Site Update', 'Comments, Multiple Characters, and More!', '', 'Check out what''s new on Audifan!\r\n\r\n<ol>\r\n<li>Profile Comments</li>\r\n	<ul>\r\n	<li>You can now add comments to profiles.</li>\r\n	<li>Comments are enabled by default, but they can be disabled from the "Profile Options" link on the Edit Profile page.</li>\r\n	<li>The Latest Comments module has been added to your profile by default.  It will show the last 10 comments you''ve received.</li>\r\n	<li>To view all comments and delete comments, click on the link under your profile picture or at the bottom of the Comments module.</li>\r\n	</ul>\r\n\r\n<li>Multiple Characters</li>\r\n	<ul>\r\n	<li>You can now add up to 3 characters to your profile.</li>\r\n	<li>If you designate a main character, it will appear at the top of the list.</li>\r\n	<li>The guides that show your next level, license, couple level, and love party have also been updated to highlight rows for each of your characters.</li>\r\n	</ul>\r\n\r\n<li>New "About Me" profile module.</li>\r\n\r\n<li>User Bar updates</li>\r\n	<ul>\r\n	<li>The User Bar at the very top has been updated with new icons.</li>\r\n	<li>There is also a new "My Comments" link.</li>\r\n	<li>If you have new comments, the icon will flash red and show how many new comments you have.</li>\r\n	</ul>\r\n\r\n<li>Collapsible Story Guide.</li>\r\n	<ul>\r\n	<li>The story guide now only shows the number, title, and objective of a story.  To view more info, click on the story and it will expand.  This is to help reduce the overall length of the guide.</li>\r\n	<li>You can expand or collapse all stories with the respective links.</li>\r\n	</ul>\r\n\r\n<li>You can change the timezone used on the site from the My Account page.</li>\r\n\r\n<li>The Video Player on the homepage no longer starts automatically.</li>\r\n\r\n<li>Fixes</li>\r\n	<ul>\r\n	<li>Fixed a display issue where the menu links appeared too large for the box on some browsers.</li>\r\n	<li>Fixed a display issue where submenus appeared below the video player on the homepage in Chrome.</li>\r\n	</ul>\r\n</ol>', 1307994115),
(27, 'Site Update', 'New Indefinite Item Guide', 'http://www.auditionfan.com/guides/indefitems/', '', 1308168770),
(28, 'Audition Update', 'July 8, 2011 Patch', '', 'The July patch includes:\r\n\r\n- Mission Book Update\r\n- Team Info/Titles\r\n- Difficulty Adjustment for UBP and BUBP\r\n- At least 4 New Songs\r\n- At least 100 New Items\r\n\r\nCheck out more info here:\r\n<a href="http://www.auditionfan.com/patch/20110708/">http://www.auditionfan.com/patch/20110708/</a>', 1310095266),
(29, 'Site Update', 'Team Titles', '', 'You can now add your team title to your character info!\r\n\r\nIf your profile does not update immediately after changing it, please log out and log back in again.', 1310177599),
(30, 'Site News', 'Image Hotlinking', '', 'We recently blocked hotlinking for all of our images.  All images affected are inside the /img/ directory.\r\n\r\nIf you are using any of our images remotely (e.g. you posted an image on a forum by copying and pasting the URL of the image directly from Audifan), your image will no longer show up.\r\n\r\nYou can use any image on Audifan for any reason, but please host them yourself!\r\n\r\nThanks! :)', 1312521411),
(31, 'Audition Update', 'August 5, 2011 Patch', '', '- Story Mode Episode 2\r\n- 100 new Couple Battle Party NPCs\r\n- 110 new items including 4 motions\r\n- 5 new songs (<a href="/music/list/">updated song list</a>)\r\n- 3 new guitar songs\r\n\r\n<a href="/patch/20110805/">View Complete Patch Info</a>', 1312596094),
(32, 'Site Update', 'New E2 Story Guide', 'http://www.auditionfan.com/guides/story2/', '', 1312596581),
(33, 'Site Update', 'E2 Story Medals', '', 'You can now add your Episode 2 Story Medal to your character information!', 1312598213),
(34, 'Site Update', 'New Member List', '', 'Now it''s easier to see if your friends are on Audifan!\r\n\r\nCheck out the new member list here:\r\n<a href="http://www.auditionfan.com/community/memberlist/">http://www.auditionfan.com/community/memberlist/</a>', 1313129763),
(35, 'Site News', 'Character Info Bug Fixed', '', 'We just fixed a bug where you couldn''t add a new character to your profile.  The problem was caused by the new E2 story icons that were introduced on August 5.\r\n\r\nWe apologize for any confusion.\r\n\r\nIf you ever see a bug or something that doesn''t look right, please <a href="/contactus/">Contact Us</a> and we''ll get right on it.  Thank you!', 1313405439),
(36, 'Audition Update', 'August 24, 2011 Patch', '', '- Indefinite purchase price lowered to eight times the 7 day price.\r\n- 71 items in mall given the indefinite purchase option.\r\n\r\n<a href="/patch/20110824/">View More Info</a>', 1314284604),
(37, 'Site Update', 'Indef Item Guide Updated', '', 'The <a href="/guides/indefitems/">Indefinite Item Guide</a> has been updated with the new items added on August 24!\r\n\r\nImages of the new items will be added as soon as possible.\r\n\r\nAdditionally, you can now show/hide images on the list.\r\n\r\nEDIT: All images of the new items have been added.', 1314284725),
(38, 'Site News', 'Unverified Account Deletions', '', 'Starting today, if you have not verified your e-mail address within a week of signing up, your account will automatically be deleted.\r\n\r\nIf your account is deleted before you can verify your e-mail address, you can always remake your account.\r\n\r\nWe''re doing this in order to keep our database free of data that isn''t being used. This does not mean your account will be deleted if you don''t log in for a while. After verifying your e-mail address, your account will never be deleted.\r\n\r\nThanks!', 1315237898),
(39, 'Site Update', 'New Signature Generator', '', 'Instantly create a signature for your Audition character!\r\n\r\n<a href="/community/siggenerator/">http://www.auditionfan.com/community/siggenerator/</a>', 1315515101),
(40, 'Audition Update', 'September 8, 2011 Patch', '', '- Couple Shop\r\n- PR Board\r\n- 5 new songs (<a href="/music/list/" target="_blank">updated song list</a>)\r\n- 3 new guitar songs\r\n- 110 new items including the new Photo Sticker, 4 motions, and 6 faces\r\n- 45 new couple items\r\n\r\n<a href="/patch/20110908/" target="_blank">More Info</a>', 1315594757),
(41, 'Site Update', 'Improved Sig Generator', '', 'Your ring, couple name, and story medals will now appear on your sig!\r\n\r\nYou can also toggle what you want and don''t want shown from the new Options section.\r\n\r\n<a href="/community/siggenerator/">http://www.auditionfan.com/community/siggenerator</a>', 1316879621),
(42, 'Site Update', 'Favorite Songs Update', '', 'The Favorite Songs module on profiles has been updated!\r\n\r\nBy mousing over any song in the list, you can see a new list of options. From your own profile, you can delete songs, and from another person''s profile, you can add their favorites to your own list!', 1317387375),
(43, 'Audition Update', 'October 5, 2011 Patch', '', '- Love Party Renewal\r\n- 6 New Songs (<a href="/music/list/">updated song list</a>)\r\n- 2 New Guitar Songs\r\n- 136 New Items including new emoticon bubbles, 48 men''s items, 71 women''s items, 2 accessories, 4 motions, and 1 platform', 1318006619),
(44, 'Site News', 'Audifan on Facebook', '', 'Audifan is now on Facebook!\r\n\r\nLike us to get all of the latest news and updates:\r\n<a href="http://www.facebook.com/auditionfan/" target="_blank">http://www.facebook.com/auditionfan</a>', 1318622385),
(45, 'Site Update', 'Tournament & Team Info on Profiles!', '', '<ol>\r\n<li>Your tournament wins and team info will now appear on your profile!</li>\r\n	<ul>\r\n	<li>Add your tournament wins and teammates on the <a href="/community/profile/edit/character/" target="_blank">Edit Characters page</a>.</li>\r\n	<li>Tournament medals will appear in your character info on your profile, along with the number of wins. If you have more than one tournament with at least one win, medals will cycle on your profile.</li>\r\n	<li>To display your teammates'' names on your profile, you must show the "My Team" module (it is hidden by default). You can do this on the <a href="/community/profile/edit/" target="_blank">Edit Layout page</a>.</li>\r\n	</ul>\r\n<li>The <a href="/community/profile/edit/character/" target="_blank">Edit Characters page</a> has been reorganized.</li>\r\n	<ul>\r\n	<li>Character names are at the top, as tabs.  Click on a tab to view/edit that character''s info.  You can add a new character from the "Add New Character" tab.</li>\r\n	<li>There are also some additional new fields that you can enter:</li>\r\n		<ul>\r\n		<li>Gender</li>\r\n		<li>Exact EXP</li>\r\n		<li>Exact hearts (couple points)</li>\r\n		</ul>\r\n	<li>If you enter your exact EXP or hearts, your level or couple level will change to the respective matching level.</li>\r\n	<li>You can now save all changes to all characters instead of having to save each character individually.</li>\r\n	<li>You can specify which character is your main character at the bottom of the page.</li>\r\n	<li>You can also delete characters at the bottom of the page.</li>\r\n	</ul>\r\n</ol>\r\nIf you encounter any problems while using these new features, please let us know!\r\n\r\nThis update was the direct result of a suggestion made from one of our user surveys!  If you have an idea for the site, please share it with us by clicking on the "Feedback" button that is attached to the left-hand side of every page!', 1320172811),
(46, 'Site Update', 'New Sig Backgrounds', '', 'We just added 7 new signature backgrounds to the <a href="/community/siggenerator/">sig generator</a>!', 1322200445),
(47, 'Site News', 'Paging Issues Fixed', '', 'We''ve fixed the paging issues on the news homepage and the member list.\r\n\r\nPages no longer have overlapping entries.', 1322548642),
(48, 'Audition Update', 'November 30, 2011 Patch', '', '- Family Tree\r\n- Photo Studio\r\n- Audition Town Christmas Theme\r\n- 6 New Songs (<a href="/music/list/">updated song list</a>)\r\n- 1 New Guitar Mode Song\r\n- 141 New Fashion Mall Items\r\n- 48 New Couple Shop Items\r\n\r\n<a href="/patch/20111130/">View more info</a>', 1322744431),
(49, 'Site Update', '5 New Sig Backgrounds', '', 'Five new sig backgrounds made by Redbana Forums member Tuii added to the <a href="/community/siggenerator/">signature generator</a>!', 1324250724),
(50, 'Site News', '12/21 Site Maintenance', '', 'Audifan will be down for site maintenance on Wednesday, December 21, 2011 starting at 8pm PST.  The site will be down for about an hour.\r\n\r\nWe apologize for the inconvenience.', 1324313980),
(51, 'Site News', 'Login Issues', '', 'If you are having trouble logging in to Audifan, please clear your cookies and try again.\r\n\r\nFor help on how to do this in your browser, please check out this article: <a href="http://www.wikihow.com/Clear-Your-Browser''s-Cookies" target="_blank">http://www.wikihow.com/Clear-Your-Browser''s-Cookies</a>', 1325395562),
(52, 'Site Update', 'New Song Lists for Special Modes', '', 'Songs lists for Ballroom Dance, Beat Up, Guitar Mode, Night Dance, One Two Party, Rhythm Holic, and Space Pang Pang have been added to the <a href="/music/list/">song list</a>!\r\n\r\nYou can switch between lists from the new drop down menu at the top.\r\n\r\nAlso, you can now add Ballroom and Guitar songs to your Favorite Songs list!\r\n\r\n<a href="/music/list/">http://www.auditionfan.com/music/list/</a>', 1325598985),
(53, 'Site Update', 'Updated Tournament Time Guide', '/guides/tournamenttime/', '', 1326118578),
(54, 'Site Update', '100 Most Favorited Songs', '', 'Check out the top 100 songs favorited on Audifan from the new <a href="/music/mostfavorited/">Top 100 list</a>!', 1326118578),
(55, 'Site Update', 'Signature Generator Update', '', 'The sig generator will now put your couple level, teammate names, and team title on your sig!  These new features can be hidden from the Options section.\r\n\r\n<a href="/community/siggenerator/">http://www.auditionfan.com/community/siggenerator/</a>', 1327722919),
(56, 'Site Update', 'License Guide - Rewards', '', 'The rewards for passing each license have been added to the License Guide!\r\n\r\nCheck out what you''ll be getting after your next license and check out the rare sets given to higher levels.\r\n\r\n<a href="/guides/licensestats/">http://www.auditionfan.com/guides/licensestats/</a>', 1327722919),
(57, 'Audition Update', 'February 1, 2012 Patch', '', 'The February patch includes:\r\n\r\n- Block Beat Mode\r\n- Easy Mode\r\n- Over 500 new moves\r\n- 7 new songs (<a href="/music/list/">updated song list</a>)\r\n- 3 new Night songs (<a href="/music/list/night/">updated Night song list</a>)\r\n- 7 new Beat Up songs (<a href="/music/list/beatup/">updated Beat Up song list</a>)\r\n- 7 new One Two Party songs (<a href="/music/list/onetwo/">updated One Two Party song list</a>)\r\n- 21 new Block Beat songs (<a href="/music/list/blockbeat/">NEW Block Beat song list</a>)\r\n- 67 new items\r\n- 57 new Couple Shop items\r\n\r\nCheck out more details and learn how to play the new modes on the <a href="/patch/20120201/">February 1, 2012 patch page</a>!', 1328205744),
(58, 'Audition Update', 'February 21, 2012 Patch', '', 'Part 2 of the February Patch includes:\r\n\r\n- 10 new Block Beat songs (<a href="/music/list/blockbeat/">updated Block Beat song list</a>)\r\n- 60 new fashion items\r\n- 75 new items now have the indefinite purchase option (<a href="/guides/indefitems/">updated Indefinite Item Guide</a>)\r\n\r\nMore info: <a href="/patch/20120221/">http://www.auditionfan.com/patch/20120221/</a>', 1329954617),
(59, 'Site Update', 'The Audifan Patch', '/patch/audifanpatch/', '', 1330903594),
(60, 'Audition Update', 'March 7, 2012 Patch', '', '- Chance System Extension\r\n- Matching Card System\r\n- New Dance Moves (Level 10 and 11 moves)\r\n- 7 new songs\r\n- 3 new Night Dance songs\r\n- 7 new Beat Up songs\r\n- 7 new One Two Party songs\r\n- 3 new Guitar songs\r\n- 2 new Block Beat songs\r\n- 70 new fashion items\r\n- 24 new Couple Shop items\r\n\r\nCheck out more info, including how to use the new chance modes and the matching card, here: <a href="/patch/20120307/">http://www.auditionfan.com/patch/20120307/</a>', 1331222081),
(61, 'Site Update', '3/14 Site Updates', '', 'Here''s a list of some minor additions/changes we made on 3/14:\r\n\r\n<ul>\r\n<li>Notifications</li>\r\n  <ul>\r\n  <li>When you first log in, you will notice a blue box under the site menu, saying hi!  This notifications box will be used more in the future.</li>\r\n  </ul>\r\n<li>Usertips</li>\r\n  <ul>\r\n  <li>Usertips are tooltips that will show you someone''s profile picture and list of characters.</li>\r\n  <li>You will see them when you mouse over someone''s name in the following places:</li>\r\n    <ul>\r\n    <li>"Latest Comments" module on your profile</li>\r\n    <li>Profile Comments page</li>\r\n    <li>Comment History page between any two people</li>\r\n    <li>Who''s Online list on the <a href="/community/">Community homepage</a></li>\r\n    <li><a href="/community/search/">Community Search</a></li>\r\n    <li><a href="/community/memberlist/">Member List</a></li>\r\n    </ul>\r\n  </ul>\r\n<li>The font of the tournament wins count next to your tournament medals in the My Characters module on your profile now matches the rest of the site.</li>\r\n</ul>\r\n\r\nAlthough these updates are minor, bugs may still arise!  If you see anything out of the ordinary, please <a href="/contactus/">contact us</a> and we''ll get right on it!  Thanks for using Audifan!', 1331767047),
(62, 'Audition Update', 'March 21, 2012 Patch', '/patch/20120321/', '', 1332409241),
(63, 'Audition Update', 'April 4, 2012 Patch', '', '- Stories 21 through 40 of Episode 2 added to Story Mode\r\n- New Team Battle Party NPCs\r\n- 8 new songs\r\n- 3 new Night Dance songs\r\n- 17 new Beat Up songs including Hard versions of some existing songs\r\n- 8 new One Two Party songs\r\n- 3 new Guitar Mode songs\r\n- 2 new Rhythm Holic songs\r\n- 1 new Space Pang Pang song\r\n- 6 new Block Beat songs\r\n- 91 new items\r\n\r\nMore info: <a href="/patch/20120404/">http://www.auditionfan.com/patch/20120404/</a>', 1333644285),
(64, 'Site Update', '4/5 Site Updates', '', 'Here''s a list of all the April 5 site updates:\r\n\r\n<ul>\r\n<li>Updated <a href="/guides/story2/">Story Guide - Episode 2</a></li>\r\n<li>Updated song lists:</li>\r\n  <ul>\r\n  <li><a href="/music/list/">Normal Song List</a></li>\r\n  <li><a href="/music/list/night/">Night Dance Song List</a></li>\r\n  <li><a href="/music/list/beatup/">Beat Up Song List</a></li>\r\n  <li><a href="/music/list/onetwo/">One Two Party Song List</a></li>\r\n  <li><a href="/music/list/guitar/"> Guitar Mode Song List</a></li>\r\n  <li><a href="/music/list/rhythmholic/">Rhythm Holic Song List</a></li>\r\n  <li><a href="/music/list/spacepangpang/">Space Pang Pang Song List</a></li>\r\n  <li><a href="/music/list/blockbeat/">Block Beat Song List</a></li>\r\n  </ul>\r\n<li>You can <a href="/community/profile/edit/character/">edit your character</a> with the new E2 story medal, but the medal <b>will not</b> appear on your profile yet.  We are having some issues with Flash, but we will add it as soon as possible.  The new medal will appear on signatures created with the <a href="/community/siggenerator/">signature generator</a>.\r\n</ul>', 1333649899),
(65, 'Site Update', 'New Icon Guide', '', 'From icons above other players, to the circles at the bottom of the result screen, learn what all of them mean with the new Icon Guide!\r\n\r\n<a href="/guides/icons/">http://www.auditionfan.com/guides/icons/</a>', 1334520411),
(66, 'Site News', 'Audifan Server Maintenance', '', 'Over the next 48 hours, Audifan may be taken offline periodically as our hosting provider performs maintenance to apply major updates to the server''s operating system.\r\n\r\nThis update will improve server security and performance.\r\n\r\nThe site shouldn''t be down for more than 2 hours at a time.  We apologize for any inconvenience caused.', 1334660563),
(67, 'Audition Update', 'April 23, 2012 Patch', '/patch/20120423/', '', 1335278107),
(68, 'Audition Update', 'May 9, 2012 Patch', '', '- Couple Garden\r\n- 8 New Songs\r\n- 4 New Night Dance Songs\r\n- 8 New Beat Up Songs\r\n- 8 New One Two Party Songs\r\n- 5 New Block Beat Songs\r\n- 2 New Guitar Songs\r\n- 83 New Items\r\n- 26 New Couple Garden Items\r\n\r\nMore info: <a href="/patch/20120509/">http://www.auditionfan.com/patch/20120509/</a>', 1336881305),
(69, 'Audition Update', 'May 24, 2012 Patch', '/patch/20120524/', '', 1337913167),
(70, 'Site Update', 'New Couple Garden Guide', '', 'Check out all of the rewards, seeds, and items from Couple Garden with our new Couple Garden guide!\r\n\r\n<a href="http://www.auditionfan.com/guides/couplegarden/">http://www.auditionfan.com/guides/couplegarden/</a>', 1337913263),
(71, 'Site Update', 'Private Comments', '', 'You can now add private comments to your friends'' profiles.\r\n\r\nTo make a comment private, click on the "Make comment private" checkbox before clicking "Add Comment."\r\n\r\nIf you add a private comment to your friend''s profile, only you and that person can see it. If you add a private comment to your own profile, only you can see it.\r\n\r\nPrivate comments will appear on your profile (marked with a red "[Private]"), as well as your "My Comments" page. Private comments will not appear on any Comment History pages.\r\n\r\nIf you ever notice something that doesn''t look right, or if you have any other comments or feedback, please let us know! Click on the Contact Us link at the bottom of any page, or click on the Feedback button on the left side of any page.', 1338496405),
(72, 'Audition Update', 'June 6, 2012 Patch', '', '- Boys & Girls Mode\r\n- 8 New Songs\r\n- 4 New Night Dance Songs\r\n- 8 New Beat Up Songs\r\n- 8 New One Two Party Songs\r\n- 2 New Rhythm Holic Songs\r\n- 2 New Guitar Songs\r\n- 5 New Block Beat Songs\r\n- 97 New Items\r\n- 16 New Couple Garden Items\r\n\r\nMore info: <a href="http://auditionfan.com/patch/20120606/">http://auditionfan.com/patch/20120606/</a>', 1339064500),
(73, 'Site News', 'Audifan Site Maintenance', '', 'Audifan will be taken offline early tomorrow, Friday, June 8, starting at 3 am EDT (midnight PDT) while we perform a major site update.\r\n\r\nThe site will be down for about 90 minutes, or until about 4:30 am EDT (1:30 am PDT).\r\n\r\nWe apologize for any inconvenience caused.\r\n\r\nEDIT: Maintenance has been delayed. It will start at 3:30 am EDT (12:30 am PDT) and will last until 5 am EDT (2 am PDT).', 1339108271),
(74, 'Site Update', 'New Friend System', '', 'You can now add your friends to your own personal friends list with the new friend system!\r\n\r\n<ul>\r\n<li>How to send a friend request:</li>\r\n	<ul>\r\n	<li>Go to your friend''s profile and click "Add to Friends" under his/her profile picture.</li>\r\n	</ul>\r\n<li>How to view your friend requests:</li>\r\n	<ul>\r\n	<li>If someone sends you a friend request, the new "My Requests" link at the top of any page will change to "New Requests." Additionally, you will receive a notification that says "You have new requests."</li>\r\n	<li>On the My Requests page, you can accept or reject a friend request as well as cancel a request that you sent to someone else.</li>\r\n	</ul>\r\n<li>How to view your friends and others'' friends:</li>\r\n	<ul>\r\n	<li>You can view your own friends by mousing over the "My Profile" tab at the top of any page and clicking "My Friends."</li>\r\n	<li>To view another person''s friends, click the "My Friends" link under his/her profile picture.</li>\r\n	</ul>\r\n<li>How to remove friends:</li>\r\n	<ul>\r\n	<li>To remove someone from your friends list, you can go to his/her profile and click the "Remove from Friends" link under his/her profile picture.</li>\r\n	<li>You can also go to your own friends list and click the [X] next to the person''s name.</li>\r\n	</ul>\r\n</ul>\r\n\r\nThere is also a new Random Friend module that you can add to your profile. This will select a random friend from your friend list and will display their name, profile picture, and provide a link to his/her profile. This module is hidden by default, but you can show it and move it to where you want it to appear on your profile by hovering over "My Profile" at the top of any page and clicking on "Edit Profile."\r\n\r\nYou can have a maximum of 100 friends at this time, but this amount will be increased once a lot of people reach this limit.\r\n\r\nIf you find any bugs or other problems with the new friend system (or anything else on the site), please let us know! Click on the "Contact Us" link at the bottom of any page or the "Feedback" button on the left side of any page to contact us!', 1339144779),
(75, 'Event (Finished)', 'Let''s All Be Friends Event', '', '<b>This event is over.</b>\r\n\r\nTo start off our new friend system, we''re giving away 10,000 Bana Cash to two of our friends!\r\n\r\nHere''s what you have to do:\r\n1. Add <a href="/community/profile/8/">Audifan</a> as a friend (<a href="/news/view/74/">learn how here</a>).\r\n2. <a href="/community/profile/8/comments/">Leave a comment</a> (must be public, not private) on Audifan''s profile. Make sure you include your Audition In-Game Name in the comment!\r\n3. On Sunday, June 10, we will randomly select two people who left a comment. These people will each receive a $10 Ultimate Game Card code!\r\n\r\nThe code will be sent by tr848 to your Audition Inbox (in game).\r\n\r\n\r\n<u>Additional Information</u>\r\n- The first comment that you leave with your In-Game Name will be considered. Additional comments will not be considered.\r\n- Two people will win the prize.\r\n- The prize for being selected is the code from one $10 Ultimate Game Card. We are not responsible if the code does not work. We are not responsible if you share your account and someone else redeems the code.\r\n- There will be no prize substitutions.\r\n- You have until 11:59 pm EDT on Saturday, June 9, 2012 to post a comment and add Audifan as a friend.\r\n- We will not send the code to you in any other way. The code will only be sent to your inbox in Audition, published by Redbana (AAU).', 1339204948),
(76, 'Event', 'Let''s All Be Friends Event Winners', '', 'The winners for the <a href="/news/view/75/">Let''s All Be Friends Event</a> have been chosen!\r\n\r\n.... The winners are ....\r\n\r\n<a href="http://auditionfan.com/community/profile/462/">Angelico</a> and <a href="http://auditionfan.com/community/profile/464/">Brennie</a>!\r\n\r\nPrizes will be sent to your Audition inbox sometime this week. Thank you to everyone who participated!!\r\n\r\nBelow is a video of how we chose the winners. Sorry for the poor quality. If you view the video in fullscreen mode, you can read it a little more easily.\r\n\r\n<iframe width="480" height="370" src="http://www.youtube.com/embed/oiakvJOQtCs" frameborder="0" allowfullscreen></iframe>', 1339352242),
(77, 'Audition Update', 'June 20, 2012 Patch', '/patch/20120620/', '', 1340288250),
(78, 'Audition News', 'RedbanaMusic', '', 'On July 10th, Redbana will be lauching the beta of their new music service for Audition called RedbanaMusic.\r\n\r\nThe service will allow you to purchase premium songs to play in Audition.\r\n\r\nIn game, there will be a new tab on the song list called "My Music." All of your purchases from RedbanaMusic will appear here.\r\n\r\nView the announcement from Dozens here: <a href="http://forums.redbana.com/showthread.php?t=58114" target="_blank">http://forums.redbana.com/showthread.php?t=58114</a>\r\n\r\nView the official press release from Redbana here:\r\n<a href="http://gamasutra.com/view/pressreleases/173247/Redbana_to_Launch_RedbanaMusic_a_Digital_Music_DistributionPlatform_for_Audition_Online.php" target="_blank">http://gamasutra.com/view/pressreleases/173247/Redbana...</a>\r\n\r\nStay with Audifan for more news about this unprecedented feature!', 1341653854),
(79, 'Audition Update', 'July 10, 2012 Patch', '', '- Fashion Week Mode\r\n- RedbanaMusic Premium Songs\r\n- 7 New Songs (<a href="/music/list/">updated song list</a>)\r\n- 5 New Premium Songs (<a href="/music/list/premium/">updated song list</a>)\r\n- 7 New Night Dance Songs (<a href="/music/list/night/">updated song list</a>)\r\n- 11 New Beat Up Songs (<a href="/music/list/beatup/">updated song list</a>)\r\n- 12 New One Two Party Songs (<a href="/music/list/onetwo/">updated song list</a>)\r\n- 2 New Space Pang Pang Songs (<a href="/music/list/spacepangpang/">updated song list</a>)\r\n- 1 New Guitar Song (<a href="/music/list/guitar/">updated song list</a>)\r\n- 5 New Block Beat Songs (<a href="/music/list/blockbeat/">updated song list</a>)\r\n- 91 New Items\r\n- 46 New Couple Shop Items\r\n\r\nMore info here: <a href="/patch/20120710/">http://auditionfan.com/patch/20120710/</a>', 1341914857),
(80, 'Site Update', 'Song List Update', '', 'The <a href="/music/list/">song list</a> has been updated with some new features!\r\n\r\n- New <a href="/music/list/premium/">premium song list</a>.\r\n- Premium songs will be highlighted purple and will have the same "D" icon next their titles as they have in Audition.\r\n- You can now hide Audition songs, so you don''t have to scroll so much to get past them. Click the new check box at the top of the list to do so.', 1341915300),
(81, 'Site News', 'Audifan.net', '', 'You can now visit Audifan at a shorter address!\r\n\r\nwww.audifan.net\r\n\r\nAudifan.net currently redirects to Auditionfan.com, but it will eventually go in the other direction. Auditionfan.com will redirect to Audifan.net.\r\n\r\nAuditionfan.com will still work, but if you have any bookmarks, we recommend that you change them to Audifan.net.', 1342208075),
(82, 'Site Update', 'Character Icons Fixed & Improved', '', 'The character icons on profiles have (finally) been fixed!\r\n\r\nThe pink E2 for completing stories 1 - 40 will now show up. Sorry it took so long to fix this.\r\n\r\nAlso, your guitar tournament wins can now be added to your character information.', 1342387911),
(84, 'Audition Update', 'July 24, 2012 Patch', '/patch/20120724/', '', 1343167228),
(85, 'Site Update', 'Log In with Facebook', '', 'You can now log in with Facebook on Audifan!\r\n\r\nTo do so, follow these steps:\r\n1. Click on the new button at the top-right corner of any page. If you are already logged in, you must log out.\r\n2. You will be prompted by Facebook to log in, if you aren''t logged in already, and to authorize Audifan to be able to access your basic info. The only info that we will use is your Facebook ID and name (both of which are visible to anyone who views your profile).\r\n3. After you authorize us, you must then connect your Facebook account to your existing Audifan account. If you do not already have an Audifan account, you can create one on the same page.\r\n4. After that, you''re done! You can simply click the Log In with Facebook button and instantly log in to your Audifan account.\r\n\r\nIf you want to disconnect your Facebook account, click on the "My Account" link at the top of any page and click "Disconnect Facebook account." If you didn''t have an Audifan account before you logged in with Facebook, you will be required to enter an e-mail address and new password so that you can log in after disconnecting Facebook.\r\n\r\nThis was probably one of our most complex features that we''ve added to the site so far. We''ve done extensive testing, but issues may still arise. If you see anything that doesn''t look right, please <a href="/contactus/">let us know</a>!\r\n\r\nFor your information, our <a href="/termsofuse/">terms of use/privacy policy</a> has been updated.', 1343168126),
(86, 'Site Update', '5 New Sig Generator Backgrounds', '/community/siggenerator/', '', 1343625937),
(87, 'Audition Update', 'August 9, 2012 Patch', '', 'The 8/9/12 patch includes:\r\n\r\n- Story Mode Episode 2: Stories 41 - 60\r\n- Matching Card Update\r\n- 7 new songs\r\n- 5 new Night Dance songs\r\n- 7 new One Two Party songs\r\n- 7 new Beat Up songs\r\n- 2 new Rhythm Holic songs\r\n- 2 new Guitar songs\r\n- 2 new Block Beat songs\r\n- 84 new Mall items\r\n- 8 new Couple Garden Market items\r\n\r\nMore info: <a href="/patch/20120809/">http://www.audifan.net/patch/20120809/</a>', 1344528017),
(88, 'Site Update', 'Site Updates 8/9 - 8/10', '', 'We''ve updated a lot over the past 2 days. Here''s a list of everything that we updated!\r\n\r\n<ul>\r\n<li>Updated song lists:</li>\r\n	<ul>\r\n	<li><a href="/music/list/" target="_blank">Normal</a></li>\r\n	<li><a href="/music/list/beatup/" target="_blank">Beat Up</a></li>\r\n	<li><a href="/music/list/blockbeat/" target="_blank">Block Beat</a></li>\r\n	<li><a href="/music/list/guitar/" target="_blank">Guitar Mode</a></li>\r\n	<li><a href="/music/list/night/" target="_blank">Night Dance</a></li>\r\n	<li><a href="/music/list/onetwo/" target="_blank">One Two Party</a></li>\r\n	<li><a href="/music/list/rhythmholic/" target="_blank">Rhythm Holic</a></li>\r\n	</ul>\r\n<li>Added picture of the level 91 set to the <a href="/guides/licensestats/" target="_blank">License Guide</a>.</li>\r\n<li>The third E2 medal for completing Episode 2 stories 1 - 60 can be added to your character information.  The medal will appear on your profile and on sigs created with the <a href="/community/siggenerator/" target="_blank">signature generator</a>. We know that the quality is pretty bad right now, but we''ll work on making it better.</li>\r\n<li>Updated <a href="/guides/story2/" target="_blank">Story Guide - Episode 2</a> with the new stories and an updated story map.</li>\r\n<li>Updated <a href="/guides/couplegarden/" target="_blank">Couple Garden Guide</a>. Images of stage 4 and 5 of the new seeds will be added as soon as possible.</li>\r\n</ul>', 1344656123),
(89, 'Audition Update', 'September 6, 2012 Patch', '', '- Club 3\r\n- 7 New Songs\r\n- 5 New Night Dance Songs\r\n- 7 New Beat Up Songs\r\n- 7 New One Two Party Songs\r\n- 1 New Space Pang Pang Song\r\n- 2 New Rhythm Holic Songs\r\n- 2 New Guitar Songs\r\n- 5 New Block Beat Songs\r\n- 84 New Fashion Mall Items\r\n- 10 New Couple Garden Market Items\r\n\r\nMore info: <a href="/patch/20120906/">http://www.audifan.net/patch/20120906/</a>', 1347042257),
(90, 'Site Update', 'New Couple Garden Timer', '', 'Audifan is pleased to announce the release of our new <a href="/community/gardentimer/">Garden Timer</a>!\r\n\r\nLearn how to use it here: <a href="/news/view/91/">http://www.audifan.net/news/view/91/</a>\r\n\r\nUPDATE: Fixed a bug where the mini garden timer help box appeared underneath the green scroller.\r\n\r\nUPDATE: Fixed a bug where two people could authorize each other, which caused nothing to happen (no control was granted to either person).', 1347046386),
(91, 'Site News', 'How to Use the Garden Timer', '', 'Know exactly when your garden will be ready to water/dust with the <a href="/community/gardentimer/">Garden Timer</a>!\r\n\r\nAs soon as you finish watering or dusting your plants, click on the "Start Water Timer" or "Start Dust Timer" button.\r\nIf you watered and dusted at the same time, or if you just planted new seeds, click both buttons.\r\nThis will start your water/dust countdown.  Logging out or closing your browser will not reset your timer.\r\n\r\nWhen your timer reaches 0:00, you can be notified by a watering can/duster sound, an alert window, or both.\r\nOn some browsers, the alert window may interrupt Audition by making your browser window pop up.  Make sure you disable the alert window if you''re in tournament, a battle party, etc.\r\n\r\nIf you go to other parts of the site, you will see a mini Garden Timer at the top of the page. This only appears if you have a garden timer currently running.\r\nTimers are most accurate on the main Garden Timer page, since it resynchs with the server every once in a while.  The mini timer will not play a sound or show an alert window when your timer reaches 0:00.\r\nOnly use the mini timer if you are actively browsing the site.\r\n\r\nYou can also share your timer with someone else (e.g. your couple).\r\nTo do this, one person must authorize the other to use their timer.\r\nLet''s say persons A and B want to use the same timer.\r\nPerson A would select the "I want to control my own timer, and I want to authorize someone else to also control it" option and enter person B''s display name.\r\nPerson B would then select the "I want to control someone else''s timer" and enter person A''s display name AFTER Person A has finished authorizing Person B.\r\nMake sure one person selects the second option and the other person selects the third option.  If both people select the second or both people select the third option, your timers will not go together.\r\n\r\nIf you need more help with the Garden Timer, or if you have any suggestions to improve it, feel free to <a href="/contactus/">contact us</a> with any questions or comments!', 1347046410),
(92, 'Site News', 'Garden Timer Auths Reset', '', 'All garden timer sharing authorizations have been reset because a bug was causing issues for a few people.\r\nThe bug has been fixed and everything should be working now.\r\nTime left before water/dust was not affected.', 1347075089),
(93, 'Audition Update', 'September 20, 2012 Patch', '/patch/20120920/', '', 1348147665),
(94, 'Site Update', 'VIP Status', '', 'Do you visit Audifan frequently?  Well, you''re awesome, and you deserve recognition!\r\n\r\nLogging in every day in a week (Monday to Sunday) will now earn you VIP status for the week that follows!\r\n\r\nAnd coming in October, you''ll have a chance to be rewarded for earning VIP status!  Stay tuned! <img src="/img/emotes/Grin.gif" style="vertical-align:middle;" />', 1348257802),
(95, 'Site News', 'First Week VIPs', '', 'Since we started the VIP feature on Friday of this week, if you logged in on Friday, Saturday, and Sunday, you will be given VIP status for next week.\r\n\r\nThe first full week will start Monday, September 24 and end Sunday, September 30.', 1348452359),
(96, 'Site Update', 'VIP Drawing', '', 'Each time you earn VIP status, you will now be able to enter into our VIP Drawing!\r\nIf you are a VIP, be sure to enter your IGN on <a href="/community/vipdrawing/">this page</a> so you can win!\r\n\r\nOne person will win 5,000 Bana Cash at the end of the month!\r\n\r\nMore info: <a href="/community/vipdrawing/">http://audifan.net/community/vipdrawing/</a>\r\n\r\nUPDATES:\r\n10/7: Fixed bug where page would allow you to enter your IGN if you have not earned VIP status.\r\n10/7: "Current chance of winning" display after you enter your IGN should now be correct.', 1349583219);
INSERT INTO `News` (`id`, `type`, `title`, `link`, `description`, `time`) VALUES
(97, 'Site Update', 'Diary/Guitar Controller Icons on Profiles', '', 'You can now add the diary anniversary icon and guitar controller icons to your character information for your profile.\r\n\r\nWe also fixed a bug where leaving the exact exp and exact hearts fields blank caused them to have a value of "0" the next time you edit your characters.  This caused your level or couple level to be changed to Level 1 upon saving.', 1349917529),
(98, 'Audition Update', 'October 11, 2012 Patch', '', '- Couple Date Planner\r\n- Story Mode, Episode 2, Stories 61 - 80\r\n- 8 new songs\r\n- 7 new Night Dance songs\r\n- 7 new Beat Up songs\r\n- 7 new One Two Party songs\r\n- 1 new Ballroom song\r\n- 1 new Space Pang Pang song\r\n- 1 new Rhythm Holic song\r\n- 5 new Block Beat songs\r\n- 86 new Fashion Mall items\r\n- 38 new Couple Shop items\r\n\r\n<a href="/patch/20121011/">More info here</a>', 1350010358),
(99, 'Site Update', 'Song List Changes', '', '- A song will now be highlighted as new if it is new to a mode instead of just when it''s new to the game. Before, if a song was newly added to a special mode (e.g. Block Beat or Rhythm Holic) in a patch after it was added to normal modes, it would not be highlighted on the special mode''s list as new.\r\n- The song list table will now always stay at the same width, instead of getting larger and going past the rest of the page''s width.\r\n- All song lists are now cached, so they should load much faster than before.', 1350421972),
(100, 'Site Update', 'Story Guide Updated/Improved', '', 'The <a href="/guides/story/">Story Guide</a> has been updated with the new stories added on <a href="/patches/20121011/">10/11</a>.\r\n\r\nIt took so long because we weren''t expecting new stories until the next patch, and we were right in the middle of working on the new Story Guide, which is now available!\r\n\r\nThe Story Guide now has tabs that separate stories into two groups of 50 for Episode 1 and two groups of 40 for Episode 2.\r\nThe requirement to pass each story is now listed, instead of written as a single sentence. Hopefully this makes it easier to see exactly what you need to do to complete the story.\r\n\r\nYet another update to the Story Guide is coming soon! Stay tuned!\r\n\r\nEDIT - 11:09pm: Fixed menu on the Story Guide. It now works as it should.', 1351047690),
(101, 'Audition Update', 'October 25, 2012 Patch', '/patch/20121025/', '', 1351167136),
(102, 'FYI', 'How to Redeem a $5 UGC code', '', 'In case you win the <a href="/community/vipdrawing">VIP Drawing</a>, or in any instance where you have a $5 Ultimate Game Card code, here''s how you redeem it!\r\n\r\n1. Log into the Redbana.com site.\r\n2. Click "Bana Cash" on the left side.\r\n3. In the "Select the payment method" drop-down, select PayByCash, then select the $5.00 option. Click Next.\r\n4. On the PayByCash.com page that opens, choose Ultimate Game Card as your payment method.\r\n5. Enter the code and it will subtract the balance on it from your amount due, which is $5.00, leaving you with $0.00 still to pay.\r\n\r\nIf you have any questions, please <a href="/contactus/">contact us</a>!', 1352065473),
(103, 'Audition Update', 'November 8, 2012 Patch', '', '- Couple Garden Expansion\r\n- Couple Garden Beat Mole\r\n- 5 New Songs\r\n- 5 New Night Dance Songs\r\n- 5 New Beat Up Songs\r\n- 5 New One Two Party Songs\r\n- 1 New Space Pang Pang Song\r\n- 1 New Rhythm Holic Songs\r\n- 1 New Guitar Song\r\n- 5 New Block Beat Songs\r\n- 79 New Fashion Mall Items\r\n- 12 New Couple Garden Market Items\r\n\r\n<a href="/patch/20121108/">More info</a>', 1352380301),
(104, 'Site Update', '11/8 Site Updates', '', '- Updated <a href="/music/list/">song lists</a>.\r\n- Update <a href="/guides/couplegarden/">Couple Garden Guide</a>.', 1352380374),
(105, 'Site Update', 'Small Change to VIP Drawing', '', 'On the VIP Drawing page, everyone can now see the current chances of winning, instead of only those who have entered.\r\n\r\nIf you have entered, your chance of winning will be highlighted and labeled.\r\n\r\nThis change was made in advance of a small rule change that will take place in December.  More info will be posted then.  (It''s a good change!)', 1353138070),
(106, 'Audition Update', 'November 22, 2012 Patch', '/patch/20121122/', '', 1353698159),
(107, 'Site Update', 'New Site Change Log', '', 'We do a lot of tiny updates that do not necessitate the creation of a news story.  If you notice something odd that you didn''t notice before, or if you''re just curious as to bugs that we''re fixing, check out our new change log:\r\n\r\n<a href="/changelog/">http://www.audifan.net/changelog/</a>\r\n\r\nYou can also find a link to the change log at the bottom of any page.', 1353974411),
(108, 'Site News', 'November VIP Drawing Winner', '', 'Congrats to <a href="/community/profile/889/">Doh</a> for winning the November VIP Drawing!\r\n\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/oqJEP2KS7Gs" frameborder="0" allowfullscreen></iframe>', 1354302267),
(109, 'Site Update', 'VIP Drawing Update: Multiple Winners', '', 'Now, more than one person can win the VIP Drawing!\r\n\r\nYou can see how many people will win in the current drawing from the <a href="/community/vipdrawing/">VIP Drawing page</a>!\r\n\r\nThis number depends on a few factors, and it may change throughout the month.', 1354382755),
(110, 'Audition Update', 'December 6, 2012 Patch', '', '- Secret Garden\r\n- Christmas theme added to Audition Town & the Couple Garden, and a Christmas tree added to rooms.\r\n- 5 new songs, all of which added to Night Dance, Beat Up, and One Two Party\r\n- 1 new Space Pang Pang song\r\n- 1 new Rhythm Holic song\r\n- 1 new Guitar song\r\n- 5 new Block Beat songs\r\n- 72 new Fashion Mall items\r\n- 19 new Couple Garden Market items\r\n\r\nMore info here: <a href="/patch/20121206/">http://www.audifan.net/patch/20121206</a>', 1354825521),
(111, 'Site News', 'Garden Timer Fixed', '', 'The garden timer should be fixed.\r\n\r\nWhen you water/dust, make sure you select whether or not you have seeds planted in your secret garden.  If you do, your regular garden''s plants will take longer to need something. If you do not, your garden will function like usual.', 1355021799),
(112, 'Site Update', 'VIP Meter Update', '', 'The VIP meter that shows how many times you have logged in this week will now show exactly which days you logged in.\r\n\r\nThe meter may be glitchy today (December 10), but it should start looking normal tomorrow.', 1355119666),
(113, 'Audition Update', 'December 20, 2012 Patch', '/patch/20121220/', '', 1356054760),
(122, 'Site News', 'February VIP Drawing Winner', '', 'Congrats to <a href="/community/profile/1016/">Lisa</a> for winning the February VIP Drawing!\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/pyolMEOhZAM" frameborder="0" allowfullscreen></iframe>', 1362174958),
(115, 'Site News', 'VIP Bug Fixed', '', 'We had an issue this week that caused the VIP login counter to be disabled.  The issue was fixed, but unfortunately, your logins this week were not counted.\r\n\r\nBasically, the VIP counter tracks logins based on the week number of the year.  Since the week number started over (happy new year!), the counter got messed up.\r\n\r\nThe counter worked correctly only for those who created an account on or after Monday, December 31.  In order to be fair to everyone else, your VIP status has been removed if you indeed created an account on Monday, December 31 and logged in everyday this week.\r\n\r\nEverything should be back to normal next week.  We''re really sorry about this, and it should not happen again.', 1357322500),
(116, 'Site News', 'December VIP Drawing Winners', '', 'Congrats to <a href="/community/profile/491/">Wind</a> and <a href="/community/profile/968/">Creampie</a> for winning the December VIP Drawing!\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/YJHI-7eEkOA" frameborder="0" allowfullscreen></iframe>\r\n\r\nRemember! If you change your IGN, be sure to change your IGN in the VIP Drawing!', 1357334565),
(117, 'Audition Update', 'January 11, 2013 Patch', '', '- Couple Point x2 Card\r\n- Happy Box Update\r\n- Sell x2 Beats Cards\r\n- 6 New Songs\r\n- 5 New Night Dance Songs\r\n- 5 New One Two Party Songs\r\n- 6 New Beat Up Songs\r\n- 1 New Space Pang Pang Song\r\n- 2 New Rhythm Holic Songs\r\n- 1 New Guitar Song\r\n- 5 New Block Beat Songs\r\n- 2 Songs Removed\r\n- 80 New Fashion Mall Items\r\n- 3 New Couple Garden Market Items\r\nMore info here: <a href="/patch/20130111/">http://www.audifan.net/patch/2013011/</a>', 1357902508),
(118, 'Site Update', 'New Site Header/Menu', '', 'Apart from the smaller size and different color, there were some additional changes:\r\n- Instead of four separate menus, the menu sections will now expand together.  Hopefully this makes it easier to find what you''re looking for, since you can see everything at once.\r\n- The menu links to the garden timer and signature generator are now under the "Guides & Tools" section, instead of under "Community."\r\n- The scroller (when it''s visible) and notifications are now underneath the "audifan.net" logo on the left side.\r\n- The My Requests link is now under "My Profile."', 1359573950),
(119, 'Site Update', 'What is MyAudifan?', '', 'MyAudifan is a new consolidated module on the homepage.  It gives you some "fast facts" about some of the tools and features on Audifan.\r\n\r\nCurrently, there are 3 sections of MyAudifan.  VIP Info, Garden Timer, and My Comments & Friends''  Comments.\r\n\r\nVIP Info shows:\r\n- Your status in the VIP drawing. If you''ve entered, if you haven''t entered yet, but can enter, etc.\r\n- How many days you''ve logged in this week.\r\n- Exactly which days you''ve logged in this week. This meter has been removed as a notification and will be here from now on.\r\n\r\nGarden Timer is basically a larger version of the mini garden timer that appears at the top of every page if a timer is currently running on your account.  As such, sounds and notifications do not occur with it, so if you are waiting for a timer, use the main timer via the menu (Guides & Tools -> Couple Garden Timer).\r\n\r\nMy Comments & Friends'' Comments:\r\nThis section brings together your comment histories with your friends.\r\nComments shown are:\r\n- All comments posted by you.\r\n- All comments posted on your profile.\r\n- Comments posted by your friends to their own profiles.\r\nThere is also a place for you to "Write Something to Your Friends." This will post a comment to your own profile. It''s basically a personal status update. The comment will still be publicly visible on your comments, but only your friends will see it in MyAudifan.\r\nBelow your name in your comments in MyAudifan, you will see your main character info.  To change the character that appears with your name, change your main character toward the bottom of the <a  href="/community/profile/edit/character/">Edit Characters page</a>.  You can set your main character to "(No Main Character)" if you don''t want a character displayed with your name.\r\n\r\nIn addition to these sections, if you have new comments or requests, notifications will appear at the upper right-hand corner of My Audifan.', 1359573964),
(120, 'Site News', 'January VIP Drawing Winner', '', 'Congrats to <a href="/community/profile/119/">Giang</a> for winning the January VIP Drawing!\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/2IEwkJCR6vc" frameborder="0" allowfullscreen></iframe>', 1359742087),
(121, 'Site News', 'Expiring Friend Requests', '', 'Friend requests will now expire if they are not responded to within one month.', 1360122500),
(123, 'Site News', '3/11 Site Maintenance', '', 'We will be having site maintenance March 11, 2013 from about 11:50pm EDT until 12:10am EDT.\r\n\r\nFor more info on what was done during the maintenance, check out the <a href="/changelog/">site change log</a>.', 1363049381),
(124, 'Audition Update', 'March 13, 2013 Patch', '/patch/20130313/', '', 1363193089),
(127, 'Site News', 'March VIP Drawing Winner', '', 'Congrats to <a href="/community/profile/330/">LunarViolet</a> for winning the March VIP Drawing!\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/6_0n1U7hWE0" frameborder="0" allowfullscreen></iframe>', 1365198533),
(126, 'Site News', 'April Fools!', '', 'Just so everyone is clear, the earlier news story was a joke for April Fools'' Day!\r\n\r\nThere are no site restrictions.\r\n- Accounts will never be deleted... ever.\r\n- Garden Timers can be used as often as you like.\r\n- You can use as many emotes as you like in comments.\r\n- Logging in with Facebook is not being removed.\r\n\r\nThank you for your continued support of Audifan!', 1364859152),
(128, 'Audition Update', 'April 10, 2013 Patch', '/patch/20130410/', '', 1365612467),
(129, 'Site Update', 'Deletable Files Guide', '', 'Clean up your client by deleting unused files listed on the Deletable Files Guide!\r\n\r\n<a href="/guides/deletablefiles/">http://www.audifan.net/guides/deletablefiles/</a>', 1366078807),
(130, 'Site News', 'April VIP Drawing Winner', '', 'Congrats to <a href="/community/profile/1133/">Also</a> for winning the April VIP Drawing!<br />\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/ZxpkJAgGqcw" frameborder="0" allowfullscreen></iframe>\r\n\r\nAlso, FYI, there will be 2 winners in the May drawing!!', 1367604390),
(131, 'Audition Update', 'May 8, 2013 Patch', '/patch/20130508/', '', 1368030661),
(132, 'Site Update', 'Invisible Mode/Profile Online Status', '', 'New Invisible Mode:\r\nOn the <a href="/account/myaccount/" target="_blank">My Account page</a>, you can now turn on Invisible Mode, which will remove your name from the Who''s Online list.\r\n\r\nNew Online Statuses on Profiles:\r\nWhen online, your profile will say so, and when offline, it will say when you were last online.  With Invisible Mode on, this info will not be displayed.', 1368761985),
(133, 'Site Update', 'Signature Generator Update', '', 'The <a href="/community/siggenerator/">signature generator</a> has been updated!\r\n\r\nHere''s a list of what was updated:\r\n- No longer need an account to make a sig.\r\n- More options for what you can put at the bottom of the sig. Either your couple & teammates, your tournament wins, or your own message.\r\n- Lv. 2 & 3 team titles.\r\n- Diary/Guitar Controller icons.\r\n- Characters in Korean, Chinese, Japanese, etc. now work, and won''t appear as little boxes.\r\n- 12 new backgrounds.', 1368826674),
(134, 'Site Update', 'New Couple Shop Guide', '/guides/coupleshop/', '', 1370037964),
(135, 'Site News', 'May VIP Drawing Winners', '', 'We apologize for the delay in the May drawing.\r\n\r\nCongrats to <a href="/community/profile/1708/">iCow</a> and <a href="/community/profile/396/">caearth</a> for winning!\r\n\r\nOur screen recorder did not work properly while we did the drawing, so in the spirit of fairness, we did not redo the drawing for the purpose of getting a video of the drawing. Therefore, we only have a screenshot of the results: <a href="http://i.imgur.com/oM8lzR7.png" target="_blank">http://i.imgur.com/oM8lzR7.png</a>\r\n\r\nVIP counts on the VIP Drawing page will be manually reset after midnight tonight.\r\n\r\nEDIT: VIP counts have been reset, and those who earned VIP status last week were credited with a VIP drawing entry.', 1370226620),
(136, 'Audition Update', 'June 5, 2013 Patch', '/patch/20130605/', '', 1370435305),
(137, 'Site Update', 'New VIP Ranking', '', 'See who has earned the most VIPs on the new <a href="/community/vipranking/">VIP Ranking</a>!\r\nA link to the VIP Ranking is also available under the Community tab on the menu.', 1370481683),
(138, 'Site Update', 'Peach Emotes', '', 'You can now add peach emotes to your comments!\r\n\r\nIf a peach emote command is the same as a default emote command, put a "2" after it for the peach emote. For example:\r\n;Grin = <img src="/img/emotes/Grin.gif" />\r\n;Grin2 = <img src="/img/emotes/Grin2.gif" />', 1372674609),
(139, 'Audition Update', 'July 3, 2013 Patch', '/patch/20130703/', '', 1372832025),
(140, 'Site News', 'June VIP Drawing Winner', '', 'Congrats to <a href="/community/profile/1489/">Leonardo Buares</a> for winning!\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/XvSMCYNXTys" frameborder="0" allowfullscreen></iframe>', 1373098551),
(141, 'Site News', 'Character Info Glitch Fixed', '', 'We fixed a glitch today that could have allowed you to add an unlimited number of characters to your profile.\r\n\r\nIf you added more than 3 characters, any characters added after the third one will be deleted.', 1373829966),
(142, 'Site Update', 'Character Mission Info', '', 'You can now add your characters'' mission progress to your profile.  A mission group (i.e. Normal, Hidden, etc.) will only be displayed under the character if it is above 0%.', 1373830093),
(143, 'Site Update', 'Indef Guide: New Faces & Accessories', '', 'In the <a href="/patch/20130724/">latest Audition update</a>, over 1,400 new items were given the indefinite purchase option.\r\n\r\nWe have updated the <a href="/guides/indefitems/">Indefinite Item Guide</a> with only the new faces and accessories.\r\n\r\nWe may continue to update the guide with new item prices, but we have decided that we are no longer going to add pictures of new hairs, tops, bottoms, shoes, and sets.  It''s simply impractical to create 1,400 images, and to add 1,400 images to our server at this time.\r\n\r\nWe''re very sorry about this change.  Hopefully there will be better ways to find indefinite items in Audition in the future.', 1374704204),
(144, 'Site News', 'July VIP Drawing Winner', '', 'Congrats to <a href="/community/profile/479/">Alei</a> for winning!\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/8togX_RlRss" frameborder="0" allowfullscreen></iframe>', 1375469873),
(145, 'Audition Update', 'August 7, 2013 Patch', '/patch/20130807/', '', 1375907214),
(146, 'Site News', '8/12 Site Maintenance', '', 'We will be taking the site down for maintenance at 11:30pm EDT.  The site will be down for about an hour.  We apologize for the inconvenience.', 1376354744),
(147, 'Site News', 'How to Use the Garden Timer', '', 'Firstly, we recommend Google Chrome!\r\n\r\n<ul>\r\n<li>Managing Timers</li>\r\n	<ul>\r\n	<li>You can have a maximum of 3 timers.</li>\r\n	<li>To add more timers, click the Add Another Timer link at the bottom of the page.</li>\r\n	<li>When you have more than 1 timer, you can delete any of them by clicking the Delete this Timer Link.</li>\r\n	<li>To rearrange your timers, use the Move this Timer Up/Down links.</li>\r\n	<li>To rename your timers, click the pencil next to the name.</li>\r\n	</ul>\r\n<li>Sharing Timers</li>\r\n	<ul>\r\n	<li>To share any of your timers, click the Share this Timer link.</li>\r\n	<li>After you enter the name of the person with whom you want to share a timer, they will get a Share Request.</li>\r\n	<li>Share Requests are located at the bottom of the page, under the Add Another Timer link.</li>\r\n	<li>You can only accept a Share Request if you have less than 3 timers because when you accept, the shared timer will be added in addition to any timers that you already have.</li>\r\n	</ul>\r\n<li>Notifications</li>\r\n	<ul>\r\n	<li>In-Browser Alert Boxes will appear as small pop-ups within the browser.</li>\r\n	<li>Desktop Notifications are only available with Google Chrome. They will appear as message bubbles above the taskbar.</li>\r\n	</ul>\r\n<li>Approximate Audition Time</li>\r\n	<ul>\r\n	<li>We try to keep the Audition Time shown on the garden timer page accurate to within 10 seconds.</li>\r\n	<li>When the time is green, it''s a good time to plant seeds or to do your first water/dust of the day, especially if you''re doing Garden 2.0.</li>\r\n	</ul>\r\n</ul>\r\n\r\nIf you have any questions, or if any issues come up, please use our <a href="/contactus/">Contact Us form</a>.  \r\n\r\nThank you for using Audifan!', 1376367347),
(148, 'Site Update', 'Garden Timer 2.0 Changes', '', '+ You can now have up to 3 timers.\r\n+ New Fertilize timer.\r\n+ You can now cancel timers that are running.\r\n+ New Chrome Desktop Notifications\r\n+ New Sharing Method.\r\n+ Your notification settings will now be saved.\r\n\r\nBug Fixes:\r\n- When the timers attempt to resynch with the server, the timers will now continue to run while this happens.  Previously, they stopped and waited for the updated times.  This caused the timers to freeze whenever the server was temporarily unavailable.  Now, if there''s an error, your current timers will not be affected, and you will simply be notified of the error.\r\n\r\nKnown Issues:\r\n- The timer can slow down considerably when the garden timer tab is inactive (whether the window is minimized, or another tab is being viewed) in Chrome.  We''re working on a fix.\r\n- The mini garden timer and the timer on MyAudifan do not work unless you visit the Garden Timer page first.  This will be fixed soon.\r\n- When you try to stop sharing a timer, you may have to log out and back in first before it goes through.  We''re working on a fix.\r\n\r\nFor more information on how to use the new Garden Timer, see the "How To" article here:\r\n<a href="/news/view/147/">http://www.audifan.net/news/view/147/</a>', 1376367395),
(149, 'Site News', 'Garden Timer Sharing Changes', '', 'We changed the way timer sharing is done, so we had to clear all previous sharing information.  If you shared your timer previously, you will have to re-connect with the other person.', 1376367425),
(150, 'Site News', 'Facebook Login Issues', '', 'Facebook is having issues, which is causing the Facebook login button to say "Invalid API Key."  It should be resolved soon.\r\nMore info here:\r\n<a href="https://developers.facebook.com/bugs/702273849788774" target="_blank">https://developers.facebook.com/bugs/702273849788774</a>\r\n\r\nEDIT 11:15pm: It''s fixed!', 1376437863),
(151, 'Audition Update', 'August 14, 2013 Patch', '/patch/20130814/', '', 1376499237),
(152, 'Site Update', 'Garden Timer Bug Fixes', '', 'We will be fixing garden timer bugs tonight and over the next few days.  We''ll update this post each time something is changed on the site.\r\n\r\n8/18 - 7:00pm\r\n(Unrelated) Fixed the menu.\r\n\r\n8/18 - 7:15pm\r\nTimer info is now correctly retrieved, even if you don''t go to the main timer page first.  Should fix myAudifan, the mini timer, and notifications.\r\nChanged the myAudifan module to say "Primary Garden Timer."  Only your topmost timer will appear in myAudifan and on the mini timer.\r\n\r\n8/18 - 7:45pm\r\nWe sort of fixed the slow down issue in Chrome.  It''s not perfect, but it''s better than it was before.  Plus, the new timer resynchs every 5 minutes (the old timer was every 10 minutes).\r\n\r\n8/19 - 5:41am\r\nThe changes from the previous update were reverted.  A new fix was applied, and it seems to be working better.', 1376867944),
(153, 'Audition News', 'Redbana Forums are back online!', 'http://forums.redbana.com', '', 1377253954),
(154, 'Site News', 'Facebook Permissions Update', '', 'When you log in with Facebook, you''ll notice that it will ask for your permission to allow Audifan to post on your behalf.\r\n\r\nWe will only post on your behalf after you review what is to be posted.  We will never add a post to your Timeline without your permission.', 1377638061),
(155, 'Site News', 'New Manual Patches', '/patch/manualpatches/', '', 1377690342),
(156, 'Site News', 'August VIP Drawing Winner', '', 'Congrats to <a href="/community/profile/1986/">Monoxi</a> for winning the August VIP Drawing!\r\n<iframe width="500" height="281" src="http://www.youtube.com/embed/tBVnBkiqlnY" frameborder="0" allowfullscreen></iframe>', 1377892504),
(157, 'Audition Update', 'September 4, 2013 Patch', '/patch/20130904/', '', 1378337096),
(158, 'Audition Update', 'September 11, 2013 Patch', '/patch/20130911/', '', 1378934861),
(159, 'Audition Update', 'September 18, 2013 Patch', '', '- 106 New Mall Items\r\n- 6 New Couple Shop Items\r\n\r\nMore info: <a href="/patch/20130917/">September 17, 2013 Patch</a>\r\n\r\n<a href="/patch/manualpatches/">Manual Patches</a>', 1379550000),
(160, 'Audition Update', 'September 25, 2013 Patch', '', '- New items now have the indefinite purchase option.\r\n- 6 New Couple Garden Items\r\n\r\nMore info: <a href="/patch/20130925/">September 25, 2013 Patch</a>\r\n\r\n<a href="/patch/manualpatches/">Manual Patches</a>', 1380126695),
